    startTimer = () => {
        const currentTime = this.state.timer;
        setInterval(() => {
            this.setState(
                {
                    timer: currentTime + 1
                },
                () => console.log(this.state.timer)
            );
        }, 1000);
    };

    handleChange = value => {
        this.setState({ salaryInput: value });
    };

    handleSubmit = event => {
        event.preventDefault();
        console.log(event.target.salaryInput.value);
    };
    render() {
        return (
            <div className="App">
                <form onSubmit={this.handleSubmit}>
                    <input
                        name="salaryInput"
                        type="text"
                        onChange={e => this.handleChange(e.target.value)}
                        value={this.state.salaryInput}
                    />
                    <button onClick={() => this.startTimer} type="submit">
                        Submit!
                    </button>
                    <button onClick={() => this.startTimer()}>
                        Start Timer
                    </button>
                </form>
                <div>
                    <span>{this.state.timer}</span>
                </div>
            </div>
        );
    }
}

export default App;
