import React, { Component } from "react";

import "./App.css";

class App extends Component {
    state = {
        seconds: 0,
        minutes: 0,
        hours: 0,
        salary: 0
    };

    addOne = currentValue => {
        this.setState({
            timer: (currentValue += 1)
        });
    };

    startTimer = currentValue => {
        setInterval(() => {
            this.setState({
                seconds: currentValue +=1
            });

            if(this.state.seconds > 60){
                this.setState({
                    seconds: 0
                })
            }
        }, 1000);


    };



    handleChange = value => {
        this.setState({ salaryInput: value });
    };

    handleSubmit = event => {
        event.preventDefault();
        console.log(event.target.salaryInput.value);
    };
    render() {
        const {salary} = this.state;
        return (
            <div className="App">
                <div>
                    <span>{this.state.seconds}</span>
                </div>
                <input value={salary} onChange={()=> this.handleInputChange}></input>
                <button onClick={() => this.startTimer(this.state.seconds)}>
                    Start Timer
                </button>


            </div>
        );
    }
}

export default App;
