import React, { Component } from "react";

import "./App.css";

class App extends Component {
    state = {
        salaryInput: 12,
        playing: false,
        timer: 0,
        payCheck: 0
    };

    addOne = currentValue => {
        this.setState({
            timer: (currentValue += 1)
        });
    };

    startTimer = currentValue => {
        setInterval(() => {
            this.setState({
                timer: (currentValue += 1)
            });
        }, 1000);
    };

    calcPay = currentPay => {
        this.setState({
            payCheck: currentPay
        })
    }

    handleChange = value => {
        this.setState({ salaryInput: value });
    };

    handleSubmit = event => {
        event.preventDefault();
        console.log(event.target.salaryInput.value);
    };
    render() {
        return (
            <div className="App">
                <div>
                    <span>{this.state.timer}</span>
                </div>
                <button onClick={() => this.startTimer(this.state.timer)}>
                    Start Timer
                </button>

                <div>
                    <span>{this.state.payCheck}</span>
                </div>
            </div>
        );
    }
}

export default App;
