import React, { Component } from "react";

import "./App.css";

class App extends Component {
    state = {
        salaryInput: 0,
        playing: false,
        timer: 0
    };

    addOne = () => {

    };



    handleChange = value => {
        this.setState({ salaryInput: value });
    };

    handleSubmit = event => {
        event.preventDefault();
        console.log(event.target.salaryInput.value);
    };
    render() {
        return (
            <div className="App">
                <form>
                    <input
                        name="salaryInput"
                        type="text"
                        onChange={e => this.handleChange(e.target.value)}
                        value={this.state.salaryInput}
                    />
                    <button onClick={() => this.startTimer} type="submit">
                        Submit!
                    </button>

                </form>
                <div>
                    <span>{this.state.timer}</span>
                </div>
            </div>
        );
    }
}

export default App;
